import React, { Fragment, useEffect, useState } from "react";
import axios from "axios";
import ErrorComponent from "./ErrorComponent";
import TableComponent from "./TableComponent";

const Search: React.FC = () => {
    const [data, setData] = useState([]);
    const [id, setId] = useState("");
    const [isNodata, setIsNodata] = useState(false);

    /*Single fetch*/
    const fetchByIdData = () => {
      const a = [] as any
      axios
        .get(`https://fakerestapi.azurewebsites.net/api/Authors/${id}`)
        .then(result => {
          console.log();
          a.push(result.data);
          console.log(a);
          setData(a);
        })
        .catch(err => {
          setIsNodata(true);
          console.log("invalid data");
        });
    };

    /*Multi fetch*/

    const fetchAllData = () => {
      axios
        .get("https://fakerestapi.azurewebsites.net/api/Authors")
        .then(result => {
          setData(result.data);
        })
        .catch(err => {
          console.log(err);
        });
    };

    useEffect(() => {
      if (id) fetchByIdData();
      else {
        fetchAllData();
      }
    },[id]);

    const settingId = () => {
      const Id = (document.getElementById("search") as HTMLInputElement).value;
      setId(Id);
      setIsNodata(false);
    };

    document.addEventListener("click", function(event) {
      const id = (event.target as HTMLInputElement).id ;
      console.log(id!=='search');
        axios
          .delete(`https://fakerestapi.azurewebsites.net/api/Authors/${id}`)
          .then(result => {
            console.log(result);
          })
          .catch(err => {
            console.log(err);
          });
      event.stopImmediatePropagation();
    });
    return (
      <Fragment>
        <section className="main">
          <form name="login" className="search-form">
            <input
              type="text"
              placeholder="Search by id"
              name="username"
              id="search"
              required
              autoComplete="off"
              onChange={settingId}
            />
          </form>
          <div className="table-search">
            {!isNodata ? (
              <TableComponent data={data}/>
            ) : (
              <ErrorComponent />
            )}
          </div>
        </section>
      </Fragment>
    );
  };

  export default Search;
